//Mudar Tamanho
document.body.onresize = resizePlans; 



function resizePlans() {
    if (document.body.clientWidth < 991) {
      document.getElementById("clsCol").className = "col-sm-9";
      document.getElementById("clsCola").className = "col-sm-9";
      document.getElementById("clsCold").className = "col-sm-9";
     }
    else{
      document.getElementById("clsCol").className = "col-sm-4";
      document.getElementById("clsCola").className = "col-sm-4";
      document.getElementById("clsCold").className = "col-sm-4";
   }
};
  //Funçaõ ao iniciar
window.onload = initPage;
function initPage() {
   //Mudar Tamanho
   resizePlans();
   //Detecção de Mouse
   var div = document.getElementById("ulBox");
   div.mouseIsOver = false;
   div.onmouseover = function()   {
      this.mouseIsOver = true;
      document.getElementById("subtitled").style.color = "#1adb91";
      document.getElementById("subtitle").style.color = "#1adb91";
   };
   div.onmouseout = function()   {
      this.mouseIsOver = false;
      document.getElementById("subtitle").style.color = "#414141";
      document.getElementById("subtitled").style.color = "#414141";
   }
}

